mapper.select:
select * from productMiddleClassification where bigClassificationCode = #{bigClassificationCode}
and middleClassificationName LIKE CONCAT('%', #{searchClassificationName}, '%')
order by id ASC

select * from productSmallClassification
where bigClassificationCode = #{bigClassificationCode}
and middleClassificationCode = #{middleClassificationCode}

    and smallClassificationName LIKE CONCAT('%', #{searchClassificationName}, '%')

order by id ASC

select p.id, p.productCode, p.productKorName, p.productEngName, p.price, p.costPrice, pBC.bigClassificationName as bigClassificationName, pBC.bigClassificationCode as bigClassificationCode, pMC.middleClassificationName as middleClassificationName, pMC.middleClassificationCode as middleClassificationCode, pSC.smallClassificationName as smallClassificationName, pSC.smallClassificationCode as smallClassificationCode from product as p inner join productBigClassification as pBC on p.bigClassificationCode = pBC.bigClassificationCode inner join productMiddleClassification pMC on p.bigClassificationCode = pMC.bigClassificationCode and p.middleClassificationCode = pMC.middleClassificationCode inner join productSmallClassification pSC on p.bigClassificationCode = pSC.bigClassificationCode and p.middleClassificationCode = pSC.middleClassificationCode and p.smallClassificationCode = pSC.smallClassificationCode
where p.bigClassificationCode = #{bigClassificationCode}
and p.middleClassificationCode = #{middleClassificationCode}
and p.smallClassificationCode = #{smallClassificationCode}
where p.productKorName LIKE CONCAT('%', #{productName}, '%')
and p.productKorName LIKE CONCAT('%', #{productName}, '%')
where p.productCode LIKE CONCAT('%', #{productName}, '%')
and p.productCode LIKE CONCAT('%', #{productName}, '%')
order by p.id ASC
