mapper.select:
select ifnull(sum(payCard.CartAmountPaid), 0) from paymentCreditCardAndCash as payCard
where payCard.floor = 1 and payCard.isReturn = 0 and payCard.openingDate = #{openingDate}
where payCard.floor = 2 and payCard.isReturn = 0 and payCard.openingDate = #{openingDate}
where payCard.floor = 3 and payCard.isReturn = 0 and payCard.openingDate = #{openingDate}
union all select ifnull(sum(payCash.CashAmountPaid), 0) from paymentCreditCardAndCash as payCash
where payCash.floor = 1 and payCash.isReturn = 0 and payCash.openingDate = #{openingDate}
where payCash.floor = 2 and payCash.isReturn = 0 and payCash.openingDate = #{openingDate}
where payCash.floor = 3 and payCash.isReturn = 0 and payCash.openingDate = #{openingDate}

select ifnull(count(case when payCard.CartAmountPaid > 0 then 1 end), 0) as cartPaidAllCtn from paymentCreditCardAndCash as payCard
where payCard.floor = 1 and payCard.isReturn = 0 and payCard.openingDate = #{openingDate}
where payCard.floor = 2 and payCard.isReturn = 0 and payCard.openingDate = #{openingDate}
where payCard.floor = 3 and payCard.isReturn = 0 and payCard.openingDate = #{openingDate}
union all select ifnull(count(case when payCash.CashAmountPaid > 0 then 1 end ), 0) as cashPaidAllCnt from paymentCreditCardAndCash as payCash
where payCash.floor = 1 and payCash.isReturn = 0 and payCash.openingDate = #{openingDate}
where payCash.floor = 2 and payCash.isReturn = 0 and payCash.openingDate = #{openingDate}
where payCash.floor = 3 and payCash.isReturn = 0 and payCash.openingDate = #{openingDate}

select ifnull(sum(payCardandCash.discountAmount), 0) from paymentCreditCardAndCash as payCardandCash
where payCardandCash.floor = 1 and payCardandCash.isReturn = 0 and payCardandCash.openingDate = #{openingDate}
where payCardandCash.floor = 2 and payCardandCash.isReturn = 0 and payCardandCash.openingDate = #{openingDate}
where payCardandCash.floor = 3 and payCardandCash.isReturn = 0 and payCardandCash.openingDate = #{openingDate}

select ifnull(count(case when payCard.CartAmountPaid > 0 then 1 end), 0) as returnItemCnt from paymentCreditCardAndCash as payCard
where payCard.isReturn = 1 and payCard.floor = 1 and payCard.openingDate = #{openingDate}
where payCard.isReturn = 1 and payCard.floor = 2 and payCard.openingDate = #{openingDate}
where payCard.isReturn = 1 and payCard.floor = 3 and payCard.openingDate = #{openingDate}
union all select ifnull(count(case when payCash.CashAmountPaid > 0 then 1 end ), 0) as returnItemCnt from paymentCreditCardAndCash as payCash
where payCash.isReturn = 1 and payCash.floor = 1 and payCash.openingDate = #{openingDate}
where payCash.isReturn = 1 and payCash.floor = 2 and payCash.openingDate = #{openingDate}
where payCash.isReturn = 1 and payCash.floor = 3 and payCash.openingDate = #{openingDate}

select ifnull(sum(payCard.CartAmountPaid), 0) from paymentCreditCardAndCash as payCard
where payCard.isReturn = 1 and payCard.floor = 1 and payCard.openingDate = #{openingDate}
where payCard.isReturn = 1 and payCard.floor = 2 and payCard.openingDate = #{openingDate}
where payCard.isReturn = 1 and payCard.floor = 3 and payCard.openingDate = #{openingDate}
union all select ifnull(sum(payCash.CashAmountPaid), 0) from paymentCreditCardAndCash as payCash
where payCash.isReturn = 1 and payCash.floor = 1 and payCash.openingDate = #{openingDate}
where payCash.isReturn = 1 and payCash.floor = 2 and payCash.openingDate = #{openingDate}
where payCash.isReturn = 1 and payCash.floor = 3 and payCash.openingDate = #{openingDate}

select ifnull(sum(CartItems.productSumPrice), 0)
from CartItems

    where CartItems.delStatus = 0
    and CartItems.openingDate = #{openingDate}


    where CartItems.delStatus = 0
    and CartItems.floor_id = 1
    and CartItems.openingDate = #{openingDate}


    where CartItems.delStatus = 0
    and CartItems.floor_id = 2
    and CartItems.openingDate = #{openingDate}


    where CartItems.delStatus = 0
    and CartItems.floor_id = 3
    and CartItems.openingDate = #{openingDate}

select * from productBigClassification

    where bigClassificationName = #{classificationName}

order by id ASC

select * from productMiddleClassification
where bigClassificationName = #{bigClassificationName}

    and middleClassificationName = #{classificationName}

order by id ASC

select * from productSmallClassification
where middleClassificationName = #{middleClassificationName}

    and smallClassificationName = #{classificationName}

order by id ASC
